<template>
  <img
    v-if="!actualSrc"
    :alt="alt"
    :src="loadingSrc"
    class="app-image"
  >
  <img
    v-else
    v-lazy="{ src: actualSrc, loading: loadingSrc }"
    :alt="alt"
    class="app-image"
  >
</template>

<script setup>
import {ref} from "vue";
import {FileSizes} from "@/composables/file-list.js";
import {apiRoutes} from "@/router/api-routes.js";

const props = defineProps({
  lazySrc: String,
  alt: {
    type: String,
    default: '',
  },
  isLocal: {
    type: Boolean,
    default: true, // "true" to show local images and "false" to show image from server
  },
  size: {
    type: String,
    default: () => {
      return FileSizes.ORIGINAL
    },
    validator: (value) => {
      return Object.values(FileSizes).indexOf(value) !== -1
    },
  },
  loadingSrc: {
    type: String,
    default: '/image-placeholder.jpg',
  },
})

const actualSrc = ref(null)
if (props.isLocal) {
  actualSrc.value = props.lazySrc
} else {
  actualSrc.value = import.meta.env.VITE_API_BASE_URL + apiRoutes.showFile + '?file=' + props.lazySrc + '&size=' + props.size
}
</script>

<style scoped>
.app-image {
  max-width: 100%;
  max-height: 100%;
  vertical-align: middle;
  object-fit: contain;
}

/*.app-image[lazy=loading] {}*/

.app-image[lazy=error] {
  position: relative;
  min-height: 50px;
  overflow: hidden;
}

.app-image[lazy=error]::after {
  content: '';
  position: absolute;
  rotate: -24deg;
  background-color: #f47171;
  width: 90%;
  height: 2px;
  top: 36%;
  left: 50%;
  translate: -50% -50%;
  z-index: 1;
}

.app-image[lazy=error]::before {
  content: 'بارگذاری ناموفق';
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: .75rem;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  padding-top: 50%;

  background-repeat: no-repeat;
  background-position: top center;
  background-image: url("data:image/png;base64,");
  background-size: 70% 70%;
  background-color: #e0e3f6;

  border-radius: .3rem;
}
</style>
